import React, { useEffect, useState } from "react";
import Sidebar from "../components/Sidebar";
import DashboardCard from "../components/DashboardCard";
import EditableTable from "../components/EditableTable";
import TransactionTable from "../components/TransactionTable";
import "../styles/ManagerPage.css";

const ManagerPage = () => {
  const [activeTab, setActiveTab] = useState("overview");
  const [managerInfo, setManagerInfo] = useState({
    first_name: "Samantha",
    department: "Gift Shop",
  });

  // Sample data (replace later with DB query)
  const inventoryData = [
    { id: 1, name: "Plush Bear", quantity: 34, price: 25.99, type: "Toy" },
    { id: 2, name: "Park Hoodie", quantity: 12, price: 49.99, type: "Apparel" },
    { id: 3, name: "Keychain", quantity: 105, price: 5.99, type: "Accessory" },
  ];

  const transactionData = [
    { id: 1, date: "2025-10-17", customer: "Emily Chen", total: 120.45 },
    { id: 2, date: "2025-10-17", customer: "Lucas Rivera", total: 45.0 },
    { id: 3, date: "2025-10-16", customer: "Ava Patel", total: 89.99 },
  ];

  return (
    <div className="manager-layout">
      <Sidebar activeTab={activeTab} setActiveTab={setActiveTab} />

      <main className="manager-content">
        <header className="manager-header glass">
          <div>
            <h1>{managerInfo.department} Manager Dashboard</h1>
            <p>Welcome back, {managerInfo.first_name}</p>
          </div>
        </header>

        {activeTab === "overview" && (
          <section className="overview-section">
            <div className="card-grid">
              <DashboardCard title="Total Revenue" value="$53,200" />
              <DashboardCard title="Transactions" value="237" />
              <DashboardCard title="Low Stock Items" value="4" />
              <DashboardCard title="Top Staff" value="Samantha Lee" />
            </div>
          </section>
        )}

        {activeTab === "inventory" && (
          <section className="inventory-section glass">
            <h2>Inventory Management</h2>
            <EditableTable data={inventoryData} />
          </section>
        )}

        {activeTab === "transactions" && (
          <section className="transactions-section glass">
            <h2>Payment History</h2>
            <TransactionTable data={transactionData} />
          </section>
        )}
      </main>
    </div>
  );
};

export default ManagerPage;
