import React, { useEffect, useState } from "react";
import "./ManagerPage.css";

const ManagerPage = () => {
  const [managerInfo, setManagerInfo] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  // Simulated logged-in email
  const mockEmail = "giftshop.manager@themepark.com";

useEffect(() => {
  // 1️⃣ Get manager info from backend
  fetch(`http://localhost:5000/manager-info/${mockEmail}`)
    .then((res) => res.json())
    .then((info) => {
      console.log("Manager info fetched:", info);
      setManagerInfo(info);

      if (info && info.department) {
        // 2️⃣ Get department data
        fetch(`http://localhost:5000/manager/${info.department}`)
          .then((res) => res.json())
          .then((data) => {
            console.log("Department data fetched:", data);
            setData(data);
            setLoading(false);
          })
          .catch((err) => {
            console.error("Error fetching department data:", err);
            setLoading(false);
          });
      } else {
        console.warn("Manager has no department");
        setLoading(false);
      }
    })
    .catch((err) => {
      console.error("Error fetching manager info:", err);
      setLoading(false);
    });
}, []);


  if (loading) return <p className="loading">Loading manager dashboard...</p>;
  if (!managerInfo) return <p className="loading">Manager info not found.</p>;

  const { department, first_name } = managerInfo;

  return (
    <div className="manager-dashboard">
      <header className="dashboard-header">
        <h1>{first_name}'s {department.charAt(0).toUpperCase() + department.slice(1)} Dashboard</h1>
        <p>Overview of staff, inventory, and recent sales</p>
      </header>

      {/* Staff Information */}
      <section className="dashboard-section">
        <h2>Staff Information</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Job Title</th>
            </tr>
          </thead>
          <tbody>
            {data.map((emp, i) => (
              <tr key={i}>
                <td>{emp.first_name} {emp.last_name}</td>
                <td>{emp.job_title}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Department Inventory */}
      {department !== "maintenance" && (
        <section className="dashboard-section">
          <h2>Department Inventory</h2>
          <table>
            <thead>
              <tr>
                <th>Item</th>
                <th>Quantity</th>
                <th>Price ($)</th>
                <th>Type</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item, i) => (
                <tr key={i}>
                  <td>{item.item_name}</td>
                  <td>{item.quantity}</td>
                  <td>{item.price}</td>
                  <td>{item.type}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      )}

      {/* Sales Overview */}
      <section className="dashboard-section sales">
        <h2>Sales Overview</h2>
        <div className="sales-grid">
          <div className="sales-card">
            <h3>Today</h3>
            <p>${Math.floor(Math.random() * 5000).toLocaleString()}</p>
          </div>
          <div className="sales-card">
            <h3>This Week</h3>
            <p>${Math.floor(Math.random() * 20000).toLocaleString()}</p>
          </div>
          <div className="sales-card">
            <h3>This Month</h3>
            <p>${Math.floor(Math.random() * 80000).toLocaleString()}</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default ManagerPage;
