import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import DashboardCard from "../components/DashboardCard";
import EditableTable from "../components/EditableTable";
import TransactionTable from "../components/TransactionTable";
import "../styles/ManagerPage.css";

const ManagerPage = () => {
  // TODO: Get manager email from your login context/props
  const managerEmail = "giftshop.manager@themepark.com"; // REPLACE THIS with actual logged-in email from your auth
  
  const [managerInfo, setManagerInfo] = useState(null);
  const [activeTab, setActiveTab] = useState("overview");
  const [dashboardData, setDashboardData] = useState({
    staff: [],
    inventory: [],
    sales: { today: 0, week: 0, month: 0 }
  });
  const [staffDetails, setStaffDetails] = useState([]);
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [lowStock, setLowStock] = useState([]);
  const [topItems, setTopItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_BASE = "http://localhost:5000";

  useEffect(() => {
    fetchManagerInfo();
  }, []);

  useEffect(() => {
    if (managerInfo && managerInfo.department) {
      fetchAllData();
    }
  }, [managerInfo]);

  const fetchManagerInfo = async () => {
    try {
      console.log("Fetching manager info for:", managerEmail);
      const res = await fetch(`${API_BASE}/manager-info/${managerEmail}`);
      console.log("Response status:", res.status);
      
      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      
      const data = await res.json();
      console.log("Manager data received:", data);
      
      if (data.error) {
        setError(data.error);
        setLoading(false);
        return;
      }
      
      setManagerInfo(data);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching manager info:", err);
      setError(err.message);
      setLoading(false);
    }
  };

  const fetchAllData = async () => {
    if (!managerInfo) return;
    
    setLoading(true);
    const dept = managerInfo.department;
    
    try {
      const [dashRes, staffRes, transRes, stockRes, topRes] = await Promise.all([
        fetch(`${API_BASE}/manager/${dept}`),
        fetch(`${API_BASE}/manager/${dept}/staff-details`),
        fetch(`${API_BASE}/manager/${dept}/recent-transactions`),
        fetch(`${API_BASE}/manager/${dept}/low-stock`),
        fetch(`${API_BASE}/manager/${dept}/top-items`)
      ]);

      const dashData = await dashRes.json();
      const staffData = await staffRes.json();
      const transData = await transRes.json();
      const stockData = await stockRes.json();
      const topData = await topRes.json();

      setDashboardData(dashData);
      setStaffDetails(staffData);
      setRecentTransactions(transData);
      setLowStock(stockData);
      setTopItems(topData);
    } catch (err) {
      console.error("Error fetching data:", err);
    } finally {
      setLoading(false);
    }
  };

  const getDepartmentName = () => {
    if (!managerInfo) return "";
    const dept = managerInfo.department;
    if (dept === "giftshop") return "Gift Shop";
    if (dept === "foodanddrinks") return "Food & Beverages";
    if (dept === "maintenance") return "Maintenance";
    return dept;
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD"
    }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      month: "short",
      day: "numeric",
      year: "numeric"
    });
  };

  if (loading) {
    return (
      <div className="manager-layout">
        <div className="loading-container">
          <p>Loading manager information...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="manager-layout">
        <div className="loading-container">
          <div style={{ textAlign: "center" }}>
            <h2 style={{ color: "#dc2626", marginBottom: "1rem" }}>Error Loading Manager Data</h2>
            <p style={{ marginBottom: "0.5rem" }}>Error: {error}</p>
            <p style={{ fontSize: "0.9rem", color: "#66785F" }}>
              Email used: {managerEmail}
            </p>
            <p style={{ fontSize: "0.9rem", color: "#66785F", marginTop: "1rem" }}>
              Check console for more details (Press F12)
            </p>
          </div>
        </div>
      </div>
    );
  }

  if (!managerInfo) {
    return (
      <div className="manager-layout">
        <div className="loading-container">
          <p>No manager found with email: {managerEmail}</p>
        </div>
      </div>
    );
  }

  const isMaintenance = managerInfo.department === "maintenance";

  return (
    <div className="manager-layout">
      <Sidebar activeTab={activeTab} setActiveTab={setActiveTab} managerInfo={managerInfo} />
      
      <main className="manager-content">
        {activeTab === "overview" && (
          <>
            <header className="manager-header glass">
              <div>
                <h1>{getDepartmentName()} Manager Dashboard</h1>
                <p>Welcome back, {managerInfo.first_name}</p>
              </div>
            </header>

            <section className="overview-section">
              <div className="card-grid">
                <DashboardCard 
                  title="Today's Revenue" 
                  value={formatCurrency(dashboardData.sales.today)} 
                  badge="Today"
                />
                <DashboardCard 
                  title="Weekly Revenue" 
                  value={formatCurrency(dashboardData.sales.week)} 
                  badge="This Week"
                />
                <DashboardCard 
                  title="Monthly Revenue" 
                  value={formatCurrency(dashboardData.sales.month)} 
                  badge="This Month"
                />
                <DashboardCard 
                  title={isMaintenance ? "Active Jobs" : "Low Stock Items"} 
                  value={isMaintenance 
                    ? recentTransactions.filter(t => t.status !== "completed").length 
                    : lowStock.length
                  }
                  alert={!isMaintenance && lowStock.length > 0}
                />
              </div>

              {/* Top Items / Recent Activity */}
              <div className="two-column-grid">
                {!isMaintenance && topItems.length > 0 && (
                  <div className="glass section-card">
                    <h3>Top Selling Items</h3>
                    <div className="items-list">
                      {topItems.map((item, idx) => (
                        <div key={idx} className="item-row">
                          <div className="item-info">
                            <span className="item-rank">{idx + 1}</span>
                            <div>
                              <p className="item-name">{item.name}</p>
                              <p className="item-meta">{item.total_sold} units sold</p>
                            </div>
                          </div>
                          <span className="item-value">{formatCurrency(item.revenue)}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                <div className="glass section-card">
                  <h3>Staff Overview</h3>
                  <div className="items-list">
                    {staffDetails.slice(0, 5).map((staff, idx) => (
                      <div key={idx} className="item-row">
                        <div className="item-info">
                          <div className="staff-avatar">
                            {staff.first_name?.[0]}{staff.last_name?.[0]}
                          </div>
                          <div>
                            <p className="item-name">{staff.first_name} {staff.last_name}</p>
                            <p className="item-meta">{staff.job_title}</p>
                          </div>
                        </div>
                        <span className="item-meta">
                          {isMaintenance ? `${staff.active_jobs || 0} jobs` : `${staff.stores_assigned || 0} stores`}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              {/* Low Stock Alert */}
              {!isMaintenance && lowStock.length > 0 && (
                <div className="glass alert-section">
                  <h3>⚠️ Low Stock Alert</h3>
                  <div className="alert-grid">
                    {lowStock.map((item, idx) => (
                      <div key={idx} className="alert-card">
                        <p className="alert-item-name">{item.name}</p>
                        <p className="alert-store">{item.store_name}</p>
                        <p className="alert-quantity">Only {item.quantity} left</p>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </section>
          </>
        )}

        {activeTab === "staff" && (
          <section className="staff-section glass scrollable">
            <div className="section-header">
              <h2>Staff Management</h2>
            </div>
            <div style={{ padding: "1.5rem", borderBottom: "1px solid rgba(178, 201, 173, 0.3)" }}>
              <input
                type="text"
                placeholder="Search staff by name or job title..."
                className="search-bar"
                value={staffSearchQuery}
                onChange={(e) => setStaffSearchQuery(e.target.value)}
              />
            </div>
            <table className="data-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Job Title</th>
                  <th>{isMaintenance ? "Active Jobs" : "Assigned Stores"}</th>
                  <th>Details</th>
                </tr>
              </thead>
              <tbody>
                {staffDetails
                  .filter(staff => 
                    `${staff.first_name} ${staff.last_name}`.toLowerCase().includes(staffSearchQuery.toLowerCase()) ||
                    staff.job_title?.toLowerCase().includes(staffSearchQuery.toLowerCase())
                  )
                  .map((staff, idx) => (
                    <tr key={idx}>
                      <td>
                        <div className="staff-cell">
                          <div className="staff-avatar-small">
                            {staff.first_name?.[0]}{staff.last_name?.[0]}
                          </div>
                          {staff.first_name} {staff.last_name}
                        </div>
                      </td>
                      <td>{staff.job_title}</td>
                      <td>
                        <span className="badge">
                          {isMaintenance ? `${staff.active_jobs || 0} jobs` : `${staff.stores_assigned || 0} stores`}
                        </span>
                      </td>
                      <td className="details-cell">{staff.store_names || staff.job_statuses || "N/A"}</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </section>
        )}

        {activeTab === "inventory" && !isMaintenance && (
          <section className="inventory-section glass scrollable">
            <div className="section-header">
              <h2>Inventory Management</h2>
            </div>
            <EditableTable 
              data={dashboardData.inventory.map(item => ({
                id: item.item_id,
                name: item.item_name,
                store: item.store_name,
                quantity: item.quantity,
                price: item.price,
                type: item.type
              }))} 
              searchable={true} 
            />
          </section>
        )}

        {activeTab === "transactions" && (
          <section className="transactions-section glass scrollable">
            <div className="section-header">
              <h2>{isMaintenance ? "Maintenance Jobs" : "Transaction History"}</h2>
            </div>

            {!isMaintenance && (
              <div className="transaction-cards">
                <DashboardCard title="Total Revenue" value={formatCurrency(dashboardData.sales.month)} />
                <DashboardCard 
                  title="Total Transactions" 
                  value={recentTransactions.length} 
                />
                <DashboardCard 
                  title="Average Order" 
                  value={recentTransactions.length > 0 
                    ? formatCurrency(recentTransactions.reduce((sum, t) => sum + parseFloat(t.total_amount || 0), 0) / recentTransactions.length)
                    : "$0.00"
                  } 
                />
              </div>
            )}

            {isMaintenance ? (
              <table className="data-table">
                <thead>
                  <tr>
                    <th>Job ID</th>
                    <th>Ride</th>
                    <th>Description</th>
                    <th>Start Date</th>
                    <th>End Date</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {recentTransactions.map((job, idx) => (
                    <tr key={idx}>
                      <td>#{job.maintenance_id}</td>
                      <td>{job.ride_name || "N/A"}</td>
                      <td className="description-cell">{job.description}</td>
                      <td>{formatDate(job.start_date)}</td>
                      <td>{job.end_date ? formatDate(job.end_date) : "Ongoing"}</td>
                      <td>
                        <span className={`status-badge status-${job.status}`}>
                          {job.status?.replace("_", " ").toUpperCase()}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <TransactionTable 
                data={recentTransactions.map(trans => ({
                  id: trans.store_order_id,
                  date: formatDate(trans.order_date),
                  customer: trans.store_name,
                  total: parseFloat(trans.total_amount || 0),
                  status: `${trans.item_count} items`
                }))} 
                searchable={true} 
              />
            )}
          </section>
        )}

        {/* DATA ENTRY FORMS TAB */}
        {activeTab === "forms" && (
          <section className="forms-section">
            <div className="section-header glass" style={{ marginBottom: "2rem", padding: "1.5rem", borderRadius: "16px" }}>
              <h2>Data Entry Forms</h2>
              <p style={{ fontSize: "0.9rem", color: "#66785F", marginTop: "0.5rem" }}>
                Add and update records in the database
              </p>
            </div>

            <div className="forms-grid">
              {!isMaintenance && (
                <div className="glass form-card">
                  <h3>Add New Inventory Item</h3>
                  <form className="data-form" onSubmit={(e) => { e.preventDefault(); alert("Feature coming soon - connects to backend API"); }}>
                    <input type="text" placeholder="Item Name" required />
                    <input type="number" placeholder="Quantity" required />
                    <input type="number" step="0.01" placeholder="Price" required />
                    <select required>
                      <option value="">Select Type</option>
                      <option>Toy</option>
                      <option>Apparel</option>
                      <option>Accessory</option>
                      <option>Food</option>
                      <option>Beverage</option>
                    </select>
                    <button type="submit" className="form-submit-btn">Add Item</button>
                  </form>
                </div>
              )}

              {!isMaintenance && (
                <div className="glass form-card">
                  <h3>Update Inventory Quantity</h3>
                  <form className="data-form" onSubmit={(e) => { e.preventDefault(); alert("Feature coming soon - connects to backend API"); }}>
                    <select required>
                      <option value="">Select Item</option>
                      {dashboardData.inventory.slice(0, 10).map((item, idx) => (
                        <option key={idx} value={item.item_id}>{item.item_name}</option>
                      ))}
                    </select>
                    <input type="number" placeholder="New Quantity" required />
                    <button type="submit" className="form-submit-btn">Update Quantity</button>
                  </form>
                </div>
              )}

              {isMaintenance && (
                <div className="glass form-card">
                  <h3>Create Maintenance Job</h3>
                  <form className="data-form" onSubmit={(e) => { e.preventDefault(); alert("Feature coming soon - connects to backend API"); }}>
                    <input type="text" placeholder="Ride Name" required />
                    <textarea placeholder="Description" rows="3" required></textarea>
                    <input type="date" placeholder="Start Date" required />
                    <select required>
                      <option value="">Select Status</option>
                      <option>scheduled</option>
                      <option>in_progress</option>
                      <option>completed</option>
                    </select>
                    <button type="submit" className="form-submit-btn">Create Job</button>
                  </form>
                </div>
              )}

              <div className="glass form-card">
                <h3>Assign Staff Member</h3>
                <form className="data-form" onSubmit={(e) => { e.preventDefault(); alert("Feature coming soon - connects to backend API"); }}>
                  <select required>
                    <option value="">Select Staff Member</option>
                    {staffDetails.slice(0, 10).map((staff, idx) => (
                      <option key={idx} value={staff.employee_id}>
                        {staff.first_name} {staff.last_name}
                      </option>
                    ))}
                  </select>
                  <select required>
                    <option value="">Assign To</option>
                    <option>Store 1</option>
                    <option>Store 2</option>
                    <option>Maintenance Job</option>
                  </select>
                  <button type="submit" className="form-submit-btn">Assign</button>
                </form>
              </div>
            </div>
          </section>
        )}

        {/* QUERIES TAB */}
        {activeTab === "queries" && (
          <section className="queries-section">
            <div className="section-header glass" style={{ marginBottom: "2rem", padding: "1.5rem", borderRadius: "16px" }}>
              <h2>Database Queries</h2>
              <p style={{ fontSize: "0.9rem", color: "#66785F", marginTop: "0.5rem" }}>
                Run custom queries to analyze your data
              </p>
            </div>

            <div className="queries-grid">
              {!isMaintenance && (
                <div className="glass query-card">
                  <h3>Low Stock Items</h3>
                  <p className="query-description">Items with quantity below 20</p>
                  <div className="query-results">
                    {lowStock.length === 0 ? (
                      <p className="no-results">No low stock items</p>
                    ) : (
                      <table className="mini-table">
                        <thead>
                          <tr>
                            <th>Item</th>
                            <th>Quantity</th>
                            <th>Store</th>
                          </tr>
                        </thead>
                        <tbody>
                          {lowStock.map((item, idx) => (
                            <tr key={idx}>
                              <td>{item.name}</td>
                              <td style={{ color: "#dc2626", fontWeight: 600 }}>{item.quantity}</td>
                              <td>{item.store_name}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    )}
                  </div>
                </div>
              )}

              {!isMaintenance && (
                <div className="glass query-card">
                  <h3>Top Selling Items</h3>
                  <p className="query-description">Best performing products</p>
                  <div className="query-results">
                    {topItems.length === 0 ? (
                      <p className="no-results">No sales data</p>
                    ) : (
                      <table className="mini-table">
                        <thead>
                          <tr>
                            <th>Item</th>
                            <th>Units Sold</th>
                            <th>Revenue</th>
                          </tr>
                        </thead>
                        <tbody>
                          {topItems.map((item, idx) => (
                            <tr key={idx}>
                              <td>{item.name}</td>
                              <td>{item.total_sold}</td>
                              <td>{formatCurrency(item.revenue)}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    )}
                  </div>
                </div>
              )}

              <div className="glass query-card">
                <h3>Staff Assignments</h3>
                <p className="query-description">Current staff distribution</p>
                <div className="query-results">
                  <table className="mini-table">
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Job Title</th>
                        <th>{isMaintenance ? "Jobs" : "Stores"}</th>
                      </tr>
                    </thead>
                    <tbody>
                      {staffDetails.slice(0, 10).map((staff, idx) => (
                        <tr key={idx}>
                          <td>{staff.first_name} {staff.last_name}</td>
                          <td>{staff.job_title}</td>
                          <td>{isMaintenance ? staff.active_jobs : staff.stores_assigned}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>

              {!isMaintenance && (
                <div className="glass query-card">
                  <h3>Sales by Period</h3>
                  <p className="query-description">Revenue breakdown</p>
                  <div className="query-results">
                    <div style={{ padding: "1rem" }}>
                      <div style={{ marginBottom: "1rem" }}>
                        <span style={{ fontWeight: 600, color: "#4B5945" }}>Today:</span>
                        <span style={{ float: "right", fontSize: "1.2rem", fontWeight: 700, color: "#91AC8F" }}>
                          {formatCurrency(dashboardData.sales.today)}
                        </span>
                      </div>
                      <div style={{ marginBottom: "1rem" }}>
                        <span style={{ fontWeight: 600, color: "#4B5945" }}>This Week:</span>
                        <span style={{ float: "right", fontSize: "1.2rem", fontWeight: 700, color: "#91AC8F" }}>
                          {formatCurrency(dashboardData.sales.week)}
                        </span>
                      </div>
                      <div>
                        <span style={{ fontWeight: 600, color: "#4B5945" }}>This Month:</span>
                        <span style={{ float: "right", fontSize: "1.2rem", fontWeight: 700, color: "#91AC8F" }}>
                          {formatCurrency(dashboardData.sales.month)}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </section>
        )}

        {/* REPORTS TAB */}
        {activeTab === "reports" && (
          <section className="reports-section">
            <div className="section-header glass" style={{ marginBottom: "2rem", padding: "1.5rem", borderRadius: "16px" }}>
              <h2>Reports</h2>
              <p style={{ fontSize: "0.9rem", color: "#66785F", marginTop: "0.5rem" }}>
                Generate detailed reports for analysis
              </p>
            </div>

            <div className="reports-grid">
              {!isMaintenance && (
                <div className="glass report-card">
                  <h3>Sales Report</h3>
                  <div className="report-content">
                    <div className="report-stats">
                      <div className="stat-item">
                        <span className="stat-label">Daily Average</span>
                        <span className="stat-value">{formatCurrency(dashboardData.sales.today)}</span>
                      </div>
                      <div className="stat-item">
                        <span className="stat-label">Weekly Total</span>
                        <span className="stat-value">{formatCurrency(dashboardData.sales.week)}</span>
                      </div>
                      <div className="stat-item">
                        <span className="stat-label">Monthly Total</span>
                        <span className="stat-value">{formatCurrency(dashboardData.sales.month)}</span>
                      </div>
                      <div className="stat-item">
                        <span className="stat-label">Total Transactions</span>
                        <span className="stat-value">{recentTransactions.length}</span>
                      </div>
                    </div>
                    <button className="report-btn">Download PDF Report</button>
                  </div>
                </div>
              )}

              {!isMaintenance && (
                <div className="glass report-card">
                  <h3>Inventory Status Report</h3>
                  <div className="report-content">
                    <div className="report-stats">
                      <div className="stat-item">
                        <span className="stat-label">Total Items</span>
                        <span className="stat-value">{dashboardData.inventory.length}</span>
                      </div>
                      <div className="stat-item">
                        <span className="stat-label">Low Stock</span>
                        <span className="stat-value" style={{ color: "#dc2626" }}>{lowStock.length}</span>
                      </div>
                      <div className="stat-item">
                        <span className="stat-label">Total Value</span>
                        <span className="stat-value">
                          {formatCurrency(dashboardData.inventory.reduce((sum, item) => 
                            sum + (item.price * item.quantity), 0))}
                        </span>
                      </div>
                    </div>
                    <button className="report-btn">Download PDF Report</button>
                  </div>
                </div>
              )}

              <div className="glass report-card">
                <h3>Staff Performance Report</h3>
                <div className="report-content">
                  <div className="report-stats">
                    <div className="stat-item">
                      <span className="stat-label">Total Staff</span>
                      <span className="stat-value">{staffDetails.length}</span>
                    </div>
                    <div className="stat-item">
                      <span className="stat-label">Active {isMaintenance ? "Jobs" : "Assignments"}</span>
                      <span className="stat-value">
                        {staffDetails.reduce((sum, s) => 
                          sum + (isMaintenance ? (s.active_jobs || 0) : (s.stores_assigned || 0)), 0)}
                      </span>
                    </div>
                  </div>
                  <button className="report-btn">Download PDF Report</button>
                </div>
              </div>

              {isMaintenance && (
                <div className="glass report-card">
                  <h3>Maintenance Completion Report</h3>
                  <div className="report-content">
                    <div className="report-stats">
                      <div className="stat-item">
                        <span className="stat-label">Total Jobs</span>
                        <span className="stat-value">{recentTransactions.length}</span>
                      </div>
                      <div className="stat-item">
                        <span className="stat-label">Completed</span>
                        <span className="stat-value" style={{ color: "#15803d" }}>
                          {recentTransactions.filter(j => j.status === "completed").length}
                        </span>
                      </div>
                      <div className="stat-item">
                        <span className="stat-label">In Progress</span>
                        <span className="stat-value" style={{ color: "#a16207" }}>
                          {recentTransactions.filter(j => j.status === "in_progress").length}
                        </span>
                      </div>
                      <div className="stat-item">
                        <span className="stat-label">Scheduled</span>
                        <span className="stat-value" style={{ color: "#1e40af" }}>
                          {recentTransactions.filter(j => j.status === "scheduled").length}
                        </span>
                      </div>
                    </div>
                    <button className="report-btn">Download PDF Report</button>
                  </div>
                </div>
              )}
            </div>
          </section>
        )}
      </main>
    </div>
  );
};

export default ManagerPage;
